import {useState} from 'react';
import './listItem.scss';
import PlayArrowIcon from '@mui/icons-material/PlayArrow';
import AddIcon from '@mui/icons-material/Add';
import ThumbUpOutlinedIcon from '@mui/icons-material/ThumbUpOutlined';
import ThumbDownOutlinedIcon from '@mui/icons-material/ThumbDownOutlined';

export default function ListItem(props) {

  const [isHovered, setIsHovered] = useState(false);
  const trailer =
  "https://player.vimeo.com/external/371433846.sd.mp4?s=236da2f3c0fd273d2c6d9a064f3ae35579b2bbdf&profile_id=139&oauth2_token_id=57447761";

  return (
    <div 
    className="listItem" 
    style = {{left: isHovered && props.index * 225 - 50 + props.index * 2.5}}
    onMouseEnter={() => setIsHovered(true)} 
    onMouseLeave={() => setIsHovered(false)}>
        <img
        src="https://occ-0-1723-92.1.nflxso.net/dnm/api/v6/X194eJsgWBDE2aQbaNdmCXGUP-Y/AAAABU7D36jL6KiLG1xI8Xg_cZK-hYQj1L8yRxbQuB0rcLCnAk8AhEK5EM83QI71bRHUm0qOYxonD88gaThgDaPu7NuUfRg.jpg?r=4ee"
        alt=""
      />

      <video src={trailer} autoPlay={true} loop />
      <div className="itemInfo">
          <div className="icons">
            <PlayArrowIcon />
            <AddIcon />
            <ThumbUpOutlinedIcon />
            <ThumbDownOutlinedIcon />
          </div>
          <div className="itemInfoTop">
            <span>1 hour 14 minutes</span>
            <span className='limit'>+16</span>
            <span>1999</span>
          </div>
          <div className="description">
            Lorem ipsum dolor sit amet consectetur adipisicing elit. Nostrum sunt assumenda, possimus veritatis voluptate quod modi, nemo quo minus sequi itaque temporibus qui unde, odio voluptates soluta sint. Fuga, perferendis?
          </div>
          <div className="genre">Action</div>
      </div>
    </div>
  )
}
