import { useEffect, useState } from 'react';
import './listItem.scss';
import PlayArrowIcon from '@mui/icons-material/PlayArrow';
import AddIcon from '@mui/icons-material/Add';
import ThumbUpOutlinedIcon from '@mui/icons-material/ThumbUpOutlined';
import ThumbDownOutlinedIcon from '@mui/icons-material/ThumbDownOutlined';
import axios from 'axios';

export default function ListItem({ index, item }) {

  const [isHovered, setIsHovered] = useState(false);
  const [movie, setMovie] = useState({});

  console.log(movies)

  useEffect(() => {
    const getMovie = async () => {
      try {
        const res = await axios.get("movies/find/" + item, {
          headers:{
            token: "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYyNThhZDQ1YTEyNjlmM2MwY2RiMDViYSIsImlzQWRtaW4iOnRydWUsImlhdCI6MTY1MDU4MzM4NCwiZXhwIjoxNjUxMDE1Mzg0fQ.BBRKyby1Eu5BWAvURZqf40-dakHjhLMbgGfxcuHA-2Q"
          },
        });
        setMovie(res.data);
      } catch (error) {
        console.log(error)
      }
      getMovie();
    }
  }, []);

  return (
    <div
      className="listItem"
      style={{ left: isHovered && index * 225 - 50 + index * 2.5 }}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}>
      <img
        src={movie.img}
        alt=""
      />
      {isHovered && (
        <>
          <video src={movie.trailer} autoPlay={true} loop />
          <div className="itemInfo">
            <div className="icons">
              <PlayArrowIcon className='icon' />
              <AddIcon className='icon'  />
              <ThumbUpOutlinedIcon className='icon'  />
              <ThumbDownOutlinedIcon className='icon'  />
            </div>
            <div className="itemInfoTop">
              <span>{movie.duration}</span>
              <span className='limit'>{movie.limit}</span>
              <span>{movie.year}</span>
            </div>
            <div className="desc">
              {movie.desc}
            </div>
            <div className="genre">{movie.genre}</div>
          </div>
        </>
      )}
    </div>
  )
}
